// @Bitmask: 0:Supress Maintenance-Required Warnings
// @User: Standard

GEN_TYPE.__field_text:
// @DisplayName: Generator type
// @Description: Generator type
// @Values: 0:Disabled, 1:IE 650w 800w Fuel Cell, 2:IE 2.4kW Fuel Cell, 3: Richenpower
// @User: Standard
// @RebootRequired: True

GPS1_CAN_OVRIDE.__field_text:
// @DisplayName: First DroneCAN GPS NODE ID
// @Description: GPS Node id for first GPS. If 0 the gps will be automatically selected on a first-come-first-GPS basis.
// @User: Advanced

GPS2_CAN_OVRIDE.__field_text:
