// @Increment: 0.001
// @User: Standard

RNGFND9_SNR_MIN.__field_text:
// @DisplayName: Minimum signal strength
// @Description: Minimum signal strength (SNR) to accept distance
// @Range: 0 65535
// @User: Advanced

